	(55, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-09-12 03:35:40', NULL, 0, 'plain', '', 0, 0, 'info'),
	(56, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-09-12 03:35:40', NULL, 0, 'plain', '', 0, 0, 'info'),
	(57, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2023-09-12 03:39:10', NULL, 0, 'plain', '', 0, 0, 'info'),
	(58, 'klaviyo_q3_2023', 'marketing', 'en_US', 'Build long lasting relationships with Klaviyo for WooCommerce', 'Increase customer engagement and boost conversions with powerful email and SMS automation from Klaviyo. Fully integrated with your Woo store, Klaviyo enables you to send personalized communications to your customers — ensuring that the right message is delivered at the right time for maximum impact.', '{}', 'pending', 'woocommerce.com', '2023-09-12 03:35:40', NULL, 0, 'plain', '', 0, 0, 'info'),
	(59, 'wc-update-db-reminder', 'update', 'en_US', 'WooCommerce database update required', 'WooCommerce has been updated! To keep things running smoothly, we have to update your database to the newest version. The database update process runs in the background and may take a little while, so please be patient. Advanced users can alternatively update via <a href="https://github.com/woocommerce/woocommerce/wiki/Upgrading-the-database-using-WP-CLI">WP CLI</a>.', '{}', 'unactioned', 'woocommerce-core', '2023-09-12 03:35:43', NULL, 0, 'plain', '', 0, 0, 'info'),
	(60, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2023-09-12 03:35:53', NULL, 0, 'plain', '', 0, 0, 'info'),
