INSERT INTO `review_wpforo_forums` (`forumid`, `title`, `slug`, `description`, `parentid`, `icon`, `cover`, `cover_height`, `last_topicid`, `last_postid`, `last_userid`, `last_post_date`, `topics`, `posts`, `permissions`, `meta_key`, `meta_desc`, `status`, `is_cat`, `layout`, `order`, `color`) VALUES
	(1, 'General Discussion', 'main-category', '<p><span style="color: #374151;font-family: Söhne, ui-sans-serif, system-ui, -apple-system, \'Segoe UI\', Roboto, Ubuntu, Cantarell, \'Noto Sans\', sans-serif, \'Helvetica Neue\', Arial, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\', \'Noto Color Emoji\';background-color: #f7f7f8">A vibrant corner within our larger forum dedicated to open and engaging conversations on a wide range of topics! Here, you\'ll find a diverse community of individuals eager to discuss, debate, and share their thoughts, questions, and experiences.</span></p>', 0, 'fas fa-comments', 0, 150, 0, 0, 0, '0000-00-00 00:00:00', 0, 0, 'a:5:{i:1;s:4:"full";i:2;s:9:"moderator";i:3;s:8:"standard";i:4;s:9:"read_only";i:5;s:8:"standard";}', '', '', 1, 1, 2, 1, '#888888'),
	(2, 'Complaints', 'real-reviews-from-human', '<p><span style="color: #374151;font-family: Söhne, ui-sans-serif, system-ui, -apple-system, \'Segoe UI\', Roboto, Ubuntu, Cantarell, \'Noto Sans\', sans-serif, \'Helvetica Neue\', Arial, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\', \'Noto Color Emoji\';background-color: #f7f7f8">Voice your concerns and share your experiences on the Complaints Subforum. This dedicated space is here for you to provide feedback and raise issues related to online casino websites. Your feedback matters, and together, we can help improve the online gaming experience for all. Join the conversation today to make your voice heard.</span></p>', 1, 'fas fa-comments', 0, 150, 0, 0, 0, '0000-00-00 00:00:00', 0, 0, 'a:5:{i:1;s:4:"full";i:2;s:9:"moderator";i:3;s:8:"standard";i:4;s:9:"read_only";i:5;s:8:"standard";}', '', '', 1, 0, 2, 2, '#888888'),
	(3, 'Help', 'help', '<p><span style="color: #374151;font-family: Söhne, ui-sans-serif, system-ui, -apple-system, \'Segoe UI\', Roboto, Ubuntu, Cantarell, \'Noto Sans\', sans-serif, \'Helvetica Neue\', Arial, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\', \'Noto Color Emoji\';background-color: #f7f7f8"><img class="alignnone size-medium wp-image-1253" src="http://localhost/wp-content/uploads/2023/09/subforum-help-300x300.png" alt="" width="300" height="300" />In need of assistance or guidance? Look no further! The Help Subforum is your go-to destination for requesting support and help from our friendly community members. Whether you\'re troubleshooting tech problems, seeking advice, or just looking for a helping hand, our community is here to assist you. Don\'t hesitate to ask for help – we\'re all in this together!</span></p>', 1, 'fas fa-comments', 0, 150, 0, 0, 0, '0000-00-00 00:00:00', 0, 0, 'a:5:{i:1;s:4:"full";i:2;s:9:"moderator";i:3;s:8:"standard";i:4;s:9:"read_only";i:5;s:8:"standard";}', '', '', 1, 0, 2, 3, '#D32D60');
